import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../Context/UserContext';
import './Login.css'
const Login = () => {
    const { signInUser } = useContext(AuthContext)
    const [error, setError] = useState(null);
    const handleLoginUser = (e) => {
        e.preventDefault();

        const form = e.target;
        const email = form.email.value;
        const password = form.password.value;
        const confirm = form.confirm.value;

        console.log(email, password, confirm);

        if (6 > password.length) {
            setError('Password should me more than 6  characters!')
            return;
        }
        if (password !== confirm) {
            setError('Your Password did not match !')
            return;
        }
    }
    return (
        <div className='login-container'>
            <h4 className='form-tittle'>Login</h4>
            <form className='form-container' onSubmit={handleLoginUser}>
                <div className="from-control">
                    <label htmlFor="email">Email</label>
                    <input type="email" name="email" id="" required />
                </div>
                <div className="from-control">
                    <label htmlFor="password">Password</label>
                    <input type="password" name="password" id="" required />
                </div>
                <p className='error-text'>{error}</p>
                <button className='btn-submit' type='submit' value='login'>Login</button>
                <p>New to Ema-john? <Link to='/signup'> Create New Account</Link></p>
            </form>
        </div>
    );
};

export default Login;
